import React, { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";
import { Line } from "react-chartjs-2";

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const SensitivityCalculator = () => {
  const [file, setFile] = useState(null);
  const [subjectProperty, setSubjectProperty] = useState(null);
  const [comparables, setComparables] = useState([]);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post(
        "http://localhost:8080/api/calculate",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (res.data.subject_property && res.data.comparables) {
        setSubjectProperty(res.data.subject_property);
        setComparables(res.data.comparables);
        setError(null);
      } else {
        setError(res.data.error || "An error occurred.");
        setSubjectProperty(null);
        setComparables([]);
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          "An error occurred while uploading the file."
      );
      setSubjectProperty(null);
      setComparables([]);
    }
  };

  const handleReset = () => {
    setFile(null);
    setSubjectProperty(null);
    setComparables([]);
    setError(null);
  };

  // Format values as currency or percentage
  const formatCurrency = (value) => {
    if (value === "N/A") return value; // Handle "N/A" values
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
    }).format(value);
  };

  const calculateSummary = () => {
    const listings = comparables.filter((comp) => comp.comp_type === "Listing");
    const filteredComparables = comparables.filter(
      (comp) => comp.comp_type !== "Listing"
    );

    const listingCount = listings.length;
    const comparableCount = filteredComparables.length;

    if (filteredComparables.length === 0) {
      return { listingCount, comparableCount, summary: null };
    }

    const preAdjValues = filteredComparables.map((comp) => comp.pre_adj);
    const postAdjValues = filteredComparables.map((comp) => comp.post_adj);

    const preMax = Math.max(...preAdjValues);
    const preMin = Math.min(...preAdjValues);
    const preRange = preMax - preMin;
    const preMean =
      preAdjValues.reduce((a, b) => a + b, 0) / preAdjValues.length;
    const prePercentChange = (preRange / preMin) * 100;

    const postMax = Math.max(...postAdjValues);
    const postMin = Math.min(...postAdjValues);
    const postRange = postMax - postMin;
    const postMean =
      postAdjValues.reduce((a, b) => a + b, 0) / postAdjValues.length;
    const postPercentChange = (postRange / postMin) * 100;

    return {
      listingCount,
      comparableCount,
      summary: {
        preMax,
        preMin,
        preRange,
        preMean,
        prePercentChange,
        postMax,
        postMin,
        postRange,
        postMean,
        postPercentChange,
      },
    };
  };

  const { listingCount, comparableCount, summary } = calculateSummary();

  // Prepare data for the chart
  const chartData = () => {
    if (!subjectProperty || comparables.length === 0) return null;

    const preAdjValues = comparables.map((comp) => comp.pre_adj);
    const preMin = Math.min(...preAdjValues);
    const preMax = Math.max(...preAdjValues);
    const scaleMin = preMin * 0.95; // 5% less than the lowest pre-adjustment price
    const scaleMax = preMax * 1.05; // 5% more than the highest pre-adjustment price

    const labels = comparables.map((comp, index) => `Comp ${index + 1}`);
    const preAdjData = comparables.map((comp) => comp.pre_adj);
    const postAdjData = comparables.map((comp) => comp.post_adj);

    return {
      labels,
      datasets: [
        {
          label: "Pre-Adjustment",
          data: preAdjData,
          borderColor: "blue",
          backgroundColor: "blue",
          tension: 0.4,
        },
        {
          label: "Post-Adjustment",
          data: postAdjData,
          borderColor: "green",
          backgroundColor: "green",
          tension: 0.4,
        },
        {
          label: "Subject Property",
          data: Array(comparables.length).fill(subjectProperty.pre_adj),
          borderColor: "red",
          backgroundColor: "red",
          borderDash: [5, 5],
          tension: 0.4,
        },
      ],
      options: {
        scales: {
          y: {
            min: scaleMin,
            max: scaleMax,
          },
        },
      },
    };
  };

  return (
    <div className="container mt-4">
      {/* Upload Form Card */}
      <div className="card mb-4">
        <div className="card-header">
          <h2>Upload XML File</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <input
                type="file"
                className="form-control"
                onChange={handleFileChange}
                accept=".xml"
              />
            </div>
            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-primary">
                Begin Analysis
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={handleReset}
              >
                Reset Calculator
              </button>
            </div>
          </form>
          {error && <p className="text-danger mt-3">{error}</p>}
        </div>
      </div>

      {/* Chart Card */}
      {chartData() && (
        <div className="card mb-4">
          <div className="card-header">
            <h2>Adjustment Directions</h2>
          </div>
          <div className="card-body">
            <Line data={chartData()} />
          </div>
        </div>
      )}
    </div>
  );
};

export default SensitivityCalculator;
