import React, { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, Tooltip, Legend } from "chart.js";
import { Scatter } from "react-chartjs-2";

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, Tooltip, Legend);

const SensitivityCalculator = () => {
  const [file, setFile] = useState(null);
  const [subjectProperty, setSubjectProperty] = useState(null);
  const [comparables, setComparables] = useState([]);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post(
        "http://localhost:8080/api/calculate",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (res.data.subject_property && res.data.comparables) {
        setSubjectProperty(res.data.subject_property);
        setComparables(res.data.comparables);
        setError(null);
      } else {
        setError(res.data.error || "An error occurred.");
        setSubjectProperty(null);
        setComparables([]);
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          "An error occurred while uploading the file."
      );
      setSubjectProperty(null);
      setComparables([]);
    }
  };

  const handleReset = () => {
    setFile(null);
    setSubjectProperty(null);
    setComparables([]);
    setError(null);
  };

  // Format values as currency or percentage
  const formatCurrency = (value) => {
    if (value === "N/A") return value; // Handle "N/A" values
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
    }).format(value);
  };

  const formatPercent = (value) => {
    if (value === "N/A") return value; // Handle "N/A" values
    return `${parseFloat(value).toFixed(2)}%`;
  };

  const calculateSummary = () => {
    const listings = comparables.filter((comp) => comp.comp_type === "Listing");
    const filteredComparables = comparables.filter(
      (comp) => comp.comp_type !== "Listing"
    );

    const listingCount = listings.length;
    const comparableCount = filteredComparables.length;

    if (filteredComparables.length === 0) {
      return { listingCount, comparableCount, summary: null };
    }

    const preAdjValues = filteredComparables.map((comp) => comp.pre_adj);
    const postAdjValues = filteredComparables.map((comp) => comp.post_adj);

    const preMax = Math.max(...preAdjValues);
    const preMin = Math.min(...preAdjValues);
    const preRange = preMax - preMin;
    const preMean =
      preAdjValues.reduce((a, b) => a + b, 0) / preAdjValues.length;
    const prePercentChange = (preRange / preMin) * 100;

    const postMax = Math.max(...postAdjValues);
    const postMin = Math.min(...postAdjValues);
    const postRange = postMax - postMin;
    const postMean =
      postAdjValues.reduce((a, b) => a + b, 0) / postAdjValues.length;
    const postPercentChange = (postRange / postMin) * 100;

    return {
      listingCount,
      comparableCount,
      summary: {
        preMax,
        preMin,
        preRange,
        preMean,
        prePercentChange,
        postMax,
        postMin,
        postRange,
        postMean,
        postPercentChange,
      },
    };
  };

  const { listingCount, comparableCount, summary } = calculateSummary();

  // Prepare data for the scatter plot
  const scatterData = () => {
    if (!comparables.length) return null;

    const preAdjPoints = comparables.map((comp, index) => ({
      x: index + 1,
      y: comp.pre_adj,
      address: comp.address,
      postAdj: comp.post_adj,
      percentChange: ((comp.post_adj - comp.pre_adj) / comp.pre_adj) * 100,
    }));

    const postAdjPoints = comparables.map((comp, index) => ({
      x: index + 1,
      y: comp.post_adj,
      address: comp.address,
      preAdj: comp.pre_adj,
      percentChange: ((comp.post_adj - comp.pre_adj) / comp.pre_adj) * 100,
    }));

    return {
      datasets: [
        {
          label: "Pre-Adjustment",
          data: preAdjPoints,
          backgroundColor: "blue",
        },
        {
          label: "Post-Adjustment",
          data: postAdjPoints,
          backgroundColor: "green",
        },
      ],
    };
  };

  const scatterOptions = {
    plugins: {
      tooltip: {
        callbacks: {
          label: (context) => {
            const dataPoint = context.raw;
            return [
              `Address: ${dataPoint.address}`,
              `Pre-Adjustment: ${formatCurrency(dataPoint.preAdj || dataPoint.y)}`,
              `Post-Adjustment: ${formatCurrency(dataPoint.postAdj || dataPoint.y)}`,
              `Percent Change: ${formatPercent(dataPoint.percentChange)}`,
            ];
          },
        },
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: "Comparable Index",
        },
      },
      y: {
        title: {
          display: true,
          text: "Sale Price",
        },
      },
    },
  };

  return (
    <div className="container mt-4">
      {/* Upload Form Card */}
      <div className="card mb-4">
        <div className="card-header">
          <h2>Upload XML File</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <input
                type="file"
                className="form-control"
                onChange={handleFileChange}
                accept=".xml"
              />
            </div>
            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-primary">
                Begin Analysis
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={handleReset}
              >
                Reset Calculator
              </button>
            </div>
          </form>
          {error && <p className="text-danger mt-3">{error}</p>}
        </div>
      </div>

      {/* Summary Results Card */}
      {summary && (
        <div className="card mb-4">
          <div className="card-header">
            <h2>Summary Results</h2>
          </div>
          <div className="card-body">
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th></th>
                  <th>Pre-Adjustment</th>
                  <th>Post-Adjustment</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Maximum Sale Price</td>
                  <td>{formatCurrency(summary.preMax)}</td>
                  <td>{formatCurrency(summary.postMax)}</td>
                </tr>
                <tr>
                  <td>Minimum Sale Price</td>
                  <td>{formatCurrency(summary.preMin)}</td>
                  <td>{formatCurrency(summary.postMin)}</td>
                </tr>
                <tr>
                  <td>Range</td>
                  <td>{formatCurrency(summary.preRange)}</td>
                  <td>{formatCurrency(summary.postRange)}</td>
                </tr>
                <tr>
                  <td>Mean/Average</td>
                  <td>{formatCurrency(summary.preMean)}</td>
                  <td>{formatCurrency(summary.postMean)}</td>
                </tr>
                <tr>
                  <td>Percent Change</td>
                  <td>{formatPercent(summary.prePercentChange)}</td>
                  <td>{formatPercent(summary.postPercentChange)}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Table of Properties Card */}
      {comparables.length > 0 && (
        <div className="card mb-4">
          <div className="card-header">
            <h2>Comparable Properties</h2>
          </div>
          <div className="card-body">
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Property Type</th>
                  <th>Address</th>
                  <th>Pre-Adjustment Amount</th>
                  <th>Post-Adjustment Amount</th>
                  <th>Comparable Type</th>
                  <th>Total Adjustment Percent</th>
                  <th>Sale Date</th>
                </tr>
              </thead>
              <tbody>
                {comparables.map((comp, index) => (
                  <tr key={index}>
                    <td>{comp.property_type}</td>
                    <td>{comp.address}</td>
                    <td>{formatCurrency(comp.pre_adj)}</td>
                    <td>{formatCurrency(comp.post_adj)}</td>
                    <td>{comp.comp_type}</td>
                    <td>{formatPercent(comp.total_adj_percent)}</td>
                    <td>{comp.sale_date}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Scatter Plot Card */}
      {scatterData() && (
        <div className="card mb-4">
          <div className="card-header">
            <h2>Adjustment Directions</h2>
          </div>
          <div className="card-body">
            <Scatter data={scatterData()} options={scatterOptions} />
          </div>
        </div>
      )}
    </div>
  );
};

export default SensitivityCalculator;
