import React, { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement, // Import LineElement
  Tooltip,
  Legend,
} from "chart.js";
import { Scatter } from "react-chartjs-2";

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Tooltip, Legend);

const SensitivityCalculator = () => {
  const [file, setFile] = useState(null);
  const [subjectProperty, setSubjectProperty] = useState(null);
  const [comparables, setComparables] = useState([]);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post(
        "http://localhost:8080/api/calculate",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (res.data.subject_property && res.data.comparables) {
        setSubjectProperty(res.data.subject_property);
        setComparables(res.data.comparables);
        setError(null);
      } else {
        setError(res.data.error || "An error occurred.");
        setSubjectProperty(null);
        setComparables([]);
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          "An error occurred while uploading the file."
      );
      setSubjectProperty(null);
      setComparables([]);
    }
  };

  const handleReset = () => {
    setFile(null);
    setSubjectProperty(null);
    setComparables([]);
    setError(null);
  };

  const calculateSummaryCounts = () => {
    const includedProperties = comparables.filter((comp) => comp.comp_type !== "Excluded");
    const excludedProperties = comparables.filter((comp) => comp.comp_type === "Excluded");

    return {
      includedCount: includedProperties.length,
      excludedCount: excludedProperties.length,
    };
  };

  const scatterData = () => {
    if (!comparables.length) return null;

    const preAdjPoints = comparables.map((comp, index) => ({
      x: index + 1,
      y: comp.pre_adj,
      address: comp.address,
      postAdj: comp.post_adj,
      percentChange: ((comp.post_adj - comp.pre_adj) / comp.pre_adj) * 100,
      adjustmentDirection: Math.atan2(comp.post_adj - comp.pre_adj, 1), // Calculate angle
    }));

    const postAdjPoints = comparables.map((comp, index) => ({
      x: index + 1,
      y: comp.post_adj,
      address: comp.address,
      preAdj: comp.pre_adj,
      percentChange: ((comp.post_adj - comp.pre_adj) / comp.pre_adj) * 100,
    }));

    const subjectLine = Array.from({ length: comparables.length + 1 }, (_, i) => ({
      x: i,
      y: subjectProperty?.pre_adj || 0,
    }));

    return {
      datasets: [
        {
          label: "Pre-Adjustment",
          data: preAdjPoints,
          backgroundColor: "blue",
          pointRadius: 8, // Increase point size
          pointStyle: (ctx, options) => {
            const { x, y, adjustmentDirection } = options.raw;
            const size = 10; // Size of the triangle
            const angle = adjustmentDirection || 0;

            // Draw a rotated triangle
            ctx.save();
            ctx.translate(x, y);
            ctx.rotate(angle);
            ctx.beginPath();
            ctx.moveTo(0, -size); // Top point
            ctx.lineTo(size, size); // Bottom-right point
            ctx.lineTo(-size, size); // Bottom-left point
            ctx.closePath();
            ctx.fillStyle = "blue";
            ctx.fill();
            ctx.restore();
          },
        },
        {
          label: "Post-Adjustment",
          data: postAdjPoints,
          backgroundColor: "green",
          pointRadius: 6, // Standard size
          pointStyle: "circle", // Keep as circles
        },
        {
          label: "Subject Sale Price",
          data: subjectLine,
          borderColor: "red",
          borderWidth: 2,
          pointRadius: 0, // Keep the subject sale price as a line
          type: "line", // Render as a line
        },
      ],
    };
  };

  const formatCurrency = (value) => {
    if (value === "N/A") return value; // Handle "N/A" values
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
    }).format(value);
  };

  const formatPercent = (value) => {
    if (value === "N/A") return value; // Handle "N/A" values
    return `${parseFloat(value).toFixed(2)}%`;
  };

  const scatterOptions = {
    plugins: {
      tooltip: {
        callbacks: {
          label: (context) => {
            const dataPoint = context.raw || {};
            return [
              `Address: ${dataPoint.address || "N/A"}`,
              `Pre-Adjustment: ${formatCurrency(dataPoint.preAdj || dataPoint.y || 0)}`,
              `Post-Adjustment: ${formatCurrency(dataPoint.postAdj || dataPoint.y || 0)}`,
              `Percent Change: ${formatPercent(dataPoint.percentChange || 0)}`,
            ];
          },
        },
      },
      afterDatasetsDraw: (chart) => {
        const { ctx, data } = chart;

        // Draw lines between pre- and post-adjustment points
        const preDataset = data.datasets[0].data; // Pre-Adjustment
        const postDataset = data.datasets[1].data; // Post-Adjustment

        ctx.save();
        ctx.strokeStyle = "gray";
        ctx.lineWidth = 1;

        preDataset.forEach((prePoint, index) => {
          const postPoint = postDataset[index];
          if (prePoint && postPoint) {
            const x1 = chart.scales.x.getPixelForValue(prePoint.x);
            const y1 = chart.scales.y.getPixelForValue(prePoint.y);
            const x2 = chart.scales.x.getPixelForValue(postPoint.x);
            const y2 = chart.scales.y.getPixelForValue(postPoint.y);

            ctx.beginPath();
            ctx.moveTo(x1, y1);
            ctx.lineTo(x2, y2);
            ctx.stroke();
          }
        });

        ctx.restore();
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: "Comparable Index",
        },
      },
      y: {
        title: {
          display: true,
          text: "Sale Price",
        },
      },
    },
  };

  return (
    <div className="container mt-4">
      {/* Upload Form Card */}
      <div className="card mb-4">
        <div className="card-header">
          <h2>Upload XML File</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <input
                type="file"
                className="form-control"
                onChange={handleFileChange}
                accept=".xml"
              />
            </div>
            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-primary">
                Begin Analysis
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={handleReset}
              >
                Reset Calculator
              </button>
            </div>
          </form>
          {error && <p className="text-danger mt-3">{error}</p>}
        </div>
      </div>

      {/* Results Summary Card */}
      {comparables.length > 0 && (
        <div className="card mb-4">
          <div className="card-header">
            <h2>Summary Results</h2>
          </div>
          <div className="card-body">
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th></th>
                  <th>Pre-Adjustment</th>
                  <th>Post-Adjustment</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Maximum Sale Price</td>
                  <td>{formatCurrency(Math.max(...comparables.map(c => c.pre_adj)))}</td>
                  <td>{formatCurrency(Math.max(...comparables.map(c => c.post_adj)))}</td>
                </tr>
                <tr>
                  <td>Minimum Sale Price</td>
                  <td>{formatCurrency(Math.min(...comparables.map(c => c.pre_adj)))}</td>
                  <td>{formatCurrency(Math.min(...comparables.map(c => c.post_adj)))}</td>
                </tr>
                <tr>
                  <td>Average Sale Price</td>
                  <td>{formatCurrency(comparables.reduce((sum, c) => sum + c.pre_adj, 0) / comparables.length)}</td>
                  <td>{formatCurrency(comparables.reduce((sum, c) => sum + c.post_adj, 0) / comparables.length)}</td>
                </tr>
                <tr>
                  <td>Included Properties</td>
                  <td colSpan="2">{calculateSummaryCounts().includedCount}</td>
                </tr>
                <tr>
                  <td>Excluded Properties</td>
                  <td colSpan="2">{calculateSummaryCounts().excludedCount}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Properties List Card */}
      {comparables.length > 0 && (
        <div className="card mb-4">
          <div className="card-header">
            <h2>Comparable Properties</h2>
          </div>
          <div className="card-body">
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Property Type</th>
                  <th>Address</th>
                  <th>Pre-Adjustment Amount</th>
                  <th>Post-Adjustment Amount</th>
                  <th>Comparable Type</th>
                  <th>Total Adjustment Percent</th>
                  <th>Sale Date</th>
                </tr>
              </thead>
              <tbody>
                {comparables.map((comp, index) => (
                  <tr key={index}>
                    <td>{comp.property_type}</td>
                    <td>{comp.address}</td>
                    <td>{formatCurrency(comp.pre_adj)}</td>
                    <td>{formatCurrency(comp.post_adj)}</td>
                    <td>{comp.comp_type}</td>
                    <td>{formatPercent(comp.total_adj_percent)}</td>
                    <td>{comp.sale_date}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Scatter Plot Card */}
      {scatterData() && (
        <div className="card mb-4">
          <div className="card-header">
            <h2>Adjustment Directions</h2>
          </div>
          <div className="card-body">
            <Scatter data={scatterData()} options={scatterOptions} />
          </div>
        </div>
      )}
    </div>
  );
};

export default SensitivityCalculator;
