import React, { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, Tooltip, Legend } from "chart.js";
import { Scatter } from "react-chartjs-2";

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, Tooltip, Legend);

const SensitivityCalculator = () => {
  const [file, setFile] = useState(null);
  const [subjectProperty, setSubjectProperty] = useState(null);
  const [comparables, setComparables] = useState([]);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post(
        "http://localhost:8080/api/calculate",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (res.data.subject_property && res.data.comparables) {
        setSubjectProperty(res.data.subject_property);
        setComparables(res.data.comparables);
        setError(null);
      } else {
        setError(res.data.error || "An error occurred.");
        setSubjectProperty(null);
        setComparables([]);
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          "An error occurred while uploading the file."
      );
      setSubjectProperty(null);
      setComparables([]);
    }
  };

  const handleReset = () => {
    setFile(null);
    setSubjectProperty(null);
    setComparables([]);
    setError(null);
  };

  // Format values as currency or percentage
  const formatCurrency = (value) => {
    if (value === "N/A") return value; // Handle "N/A" values
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
    }).format(value);
  };

  const formatPercent = (value) => {
    if (value === "N/A") return value; // Handle "N/A" values
    return `${parseFloat(value).toFixed(2)}%`;
  };

  const calculateSummary = () => {
    const listings = comparables.filter((comp) => comp.comp_type === "Listing");
    const filteredComparables = comparables.filter(
      (comp) => comp.comp_type !== "Listing"
    );

    const listingCount = listings.length;
    const comparableCount = filteredComparables.length;

    if (filteredComparables.length === 0) {
      return { listingCount, comparableCount, summary: null };
    }

    const preAdjValues = filteredComparables.map((comp) => comp.pre_adj);
    const postAdjValues = filteredComparables.map((comp) => comp.post_adj);

    const preMax = Math.max(...preAdjValues);
    const preMin = Math.min(...preAdjValues);
    const preRange = preMax - preMin;
    const preMean =
      preAdjValues.reduce((a, b) => a + b, 0) / preAdjValues.length;
    const prePercentChange = (preRange / preMin) * 100;

    const postMax = Math.max(...postAdjValues);
    const postMin = Math.min(...postAdjValues);
    const postRange = postMax - postMin;
    const postMean =
      postAdjValues.reduce((a, b) => a + b, 0) / postAdjValues.length;
    const postPercentChange = (postRange / postMin) * 100;

    return {
      listingCount,
      comparableCount,
      summary: {
        preMax,
        preMin,
        preRange,
        preMean,
        prePercentChange,
        postMax,
        postMin,
        postRange,
        postMean,
        postPercentChange,
      },
    };
  };

  const { listingCount, comparableCount, summary } = calculateSummary();

  const scatterData = () => {
    if (!comparables.length) return null;

    const preAdjPoints = comparables.map((comp, index) => ({
      x: index + 1,
      y: comp.pre_adj,
      address: comp.address,
      postAdj: comp.post_adj,
      percentChange: ((comp.post_adj - comp.pre_adj) / comp.pre_adj) * 100,
    }));

    const postAdjPoints = comparables.map((comp, index) => ({
      x: index + 1,
      y: comp.post_adj,
      address: comp.address,
      preAdj: comp.pre_adj,
      percentChange: ((comp.post_adj - comp.pre_adj) / comp.pre_adj) * 100,
    }));

    const subjectPoint = {
      x: 0, // Subject property is at x=0
      y: subjectProperty?.pre_adj || 0,
    };

    return {
      datasets: [
        {
          label: "Pre-Adjustment",
          data: preAdjPoints,
          backgroundColor: "blue",
        },
        {
          label: "Post-Adjustment",
          data: postAdjPoints,
          backgroundColor: "green",
        },
        {
          label: "Subject Property",
          data: [subjectPoint],
          backgroundColor: "red",
          pointRadius: 8, // Larger point for subject property
        },
      ],
    };
  };

  const scatterOptions = {
    plugins: {
      tooltip: {
        callbacks: {
          label: (context) => {
            const dataPoint = context.raw;
            return [
              `Address: ${dataPoint.address}`,
              `Pre-Adjustment: ${formatCurrency(dataPoint.preAdj || dataPoint.y)}`,
              `Post-Adjustment: ${formatCurrency(dataPoint.postAdj || dataPoint.y)}`,
              `Percent Change: ${formatPercent(dataPoint.percentChange)}`,
            ];
          },
        },
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: "Comparable Index",
        },
      },
      y: {
        title: {
          display: true,
          text: "Sale Price",
        },
      },
    },
    plugins: {
      afterDatasetsDraw: (chart) => {
        const { ctx, data } = chart;

        // Draw arrows between pre- and post-adjustment points
        const preDataset = data.datasets[0].data; // Pre-Adjustment
        const postDataset = data.datasets[1].data; // Post-Adjustment

        ctx.save();
        ctx.strokeStyle = "gray";
        ctx.fillStyle = "gray";
        ctx.lineWidth = 1;

        preDataset.forEach((prePoint, index) => {
          const postPoint = postDataset[index];
          if (prePoint && postPoint) {
            // Draw line
            const x1 = chart.scales.x.getPixelForValue(prePoint.x);
            const y1 = chart.scales.y.getPixelForValue(prePoint.y);
            const x2 = chart.scales.x.getPixelForValue(postPoint.x);
            const y2 = chart.scales.y.getPixelForValue(postPoint.y);

            ctx.beginPath();
            ctx.moveTo(x1, y1);
            ctx.lineTo(x2, y2);
            ctx.stroke();

            // Draw arrowhead
            const angle = Math.atan2(y2 - y1, x2 - x1);
            const arrowLength = 10;
            const arrowWidth = 5;

            ctx.beginPath();
            ctx.moveTo(x2, y2);
            ctx.lineTo(
              x2 - arrowLength * Math.cos(angle - Math.PI / 6),
              y2 - arrowLength * Math.sin(angle - Math.PI / 6)
            );
            ctx.lineTo(
              x2 - arrowLength * Math.cos(angle + Math.PI / 6),
              y2 - arrowLength * Math.sin(angle + Math.PI / 6)
            );
            ctx.closePath();
            ctx.fill();
          }
        });

        ctx.restore();
      },
    },
  };

  return (
    <div className="container mt-4">
      {/* Upload Form Card */}
      <div className="card mb-4">
        <div className="card-header">
          <h2>Upload XML File</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <input
                type="file"
                className="form-control"
                onChange={handleFileChange}
                accept=".xml"
              />
            </div>
            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-primary">
                Begin Analysis
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={handleReset}
              >
                Reset Calculator
              </button>
            </div>
          </form>
          {error && <p className="text-danger mt-3">{error}</p>}
        </div>
      </div>

      {/* Scatter Plot Card */}
      {scatterData() && (
        <div className="card mb-4">
          <div className="card-header">
            <h2>Adjustment Directions</h2>
          </div>
          <div className="card-body">
            <Scatter data={scatterData()} options={scatterOptions} />
          </div>
        </div>
      )}
    </div>
  );
};

export default SensitivityCalculator;
